<?phpnamespace App\Http\Controllers\www;use App\Http\Controllers\Controller as Controller;use App\Models\Article;class ArchiveController extends Controller{    public function __construct()    {        // parent::__construct();        //get hot        $hot = Article::where('active', '=', 1)->orderBy('hit', 'DESC')->take(5)->get();        view()->share(['hot' => $hot]);    }    /**     * show article list     * @param  integer $page page number     * @return n/a        render page     */    public function displayList($page = 0)    {        //get list        $list = Article::where('active', '=', 1)->orderBy('created_at', 'DESC')->paginate(30);        return view('www.archive.list')->with(['list' => $list]);    }    /**     * show specified article     * @param  integer $id article id     * @return n/a      render page     */    public function displayArticle($id = 0)    {        //get article        $article = Article::find($id);        $article->hit++;        $article->save();        return view('www.archive.detail')->with(['article' => $article]);    }}